    &amp;#125;)
    // 定义一个list插槽组件
    Vue.component(&#39;skill-list-arr&#39;,&amp;#123;
        props : [&#39;item&#39;],
        template : &#39;&amp;lt;li&amp;gt;&amp;#123;&amp;#123;item&amp;#125;&amp;#125;&amp;lt;/li&amp;gt;&#39;
    &amp;#125;)
    var vm = new Vue(&amp;#123;
        el : &#39;#app&#39;,
        data : &amp;#123;
            title : &#39;技术列表&#39;,
            skillList : [&#39;java&#39;,&#39;mysql&#39;,&#39;python&#39;]
        &amp;#125;
    &amp;#125;)
